// App.js
import React from 'react';
import usePlayer from './usePlayer'; // Renamed Player to usePlayer to follow hook naming convention

function App() {
  const { name, setName, passes, setPasses, average, setAverage } = usePlayer('TEST');

  function incrementPass(passKey) {
    // Logic to increment a specific pass count
    setPasses((currentPasses) => {
      return { ...currentPasses, [passKey]: currentPasses[passKey] + 1 };
    });
  }

  return (
    <div className="container">
      <div className="button_container">
        <button onClick={() => incrementPass('3')}>3</button>
        <button onClick={() => incrementPass('2')}>2</button>
        <button onClick={() => incrementPass('1')}>1</button>
        <button onClick={() => incrementPass('0')}>0</button>
      </div>
      <div className="stats">
        <p>3: {passes["3"]}</p>
        <p>2: {passes["2"]}</p>
        <p>1: {passes["1"]}</p>
        <p>0: {passes["0"]}</p>
        <p>Average: {average}</p>
      </div>
    </div>
  );
}

export default App;
