// Custom Hook: useUser.js
import { useState } from 'react';
import Session from './Session';

const useUser = () => {
  const [username, setUsername] = useState('');
  const [sessions, setSessions] = useState(() => [
    // This function will only be executed on the initial render, providing
    // a default value for the sessions state.
    new Session(new Date().toLocaleDateString()),
    new Session(new Date().toLocaleDateString()),
    new Session("11/14/2023"),
  ]);

  const addSession = (description) => {
    setSessions((prevSessions) => [...prevSessions, new Session(description)]);
  };

  return { username, setUsername, sessions, addSession };
};

export default useUser;
