import React, { useState } from 'react';

function App() {
  const [passes, setPasses] = useState({
    "3": 0,
    "2": 0,
    "1": 0,
    "0": 0,
  })

  function incrementPass(passKey) {
    // Logic to increment a specific pass count
    setPasses((currentPasses) => {
      return { ...currentPasses, [passKey]: currentPasses[passKey] + 1 };
    });
  }



  return (
    <div className="container">  
      <div className="button_container">
        {/* <button onClick={() => incrementPass('3')}> 3 </button>
        <button onClick={() => incrementPass('2')}> 2 </button>
        <button onClick={() => incrementPass('1')}> 1 </button>
        <button onClick={() => incrementPass('0')}> 0 </button> */}
      </div>
      <div className="stats">
        {/* Ensure you are accessing the passes in a way that works with your state management */}
        <p> 3: {passes["3"]}</p>
        <p> 2: {passes["2"]}</p>
        <p> 1: {passes["1"]}</p>
        <p> 0: {passes["0"]}</p>
      </div>
    </div>
  );
}

export default App;
