import React, { useState } from 'react';

function App() {
  const [passes, setPasses] = useState({
    "3": 0,
    "2": 0,
    "1": 0,
    "0": 0,
  })

  const [average, setAverage] = useState(0)

  function incrementPass(passKey) {
    setPasses((passes) => {
      return { ...passes, [passKey]: passes[passKey] + 1 };
    });
    setAverage(updateAverage)
  }

  function updateAverage(){
      let total = 0;
      let count = 0;
      for (const key in passes) {
        total += passes[key] * key
        count += passes[key]
      }
      console.log(total)
      console.log(count)
      return (total / count); // Calculate the average
  }



  return (
    <div className="container">  
      <div className="button_container">
        <button onClick={() => incrementPass('3')}> 3 </button>
        <button onClick={() => incrementPass('2')}> 2 </button>
        <button onClick={() => incrementPass('1')}> 1 </button>
        <button onClick={() => incrementPass('0')}> 0 </button>
      </div>
      <div className="stats">
        {/* Ensure you are accessing the passes in a way that works with your state management */}
        <p> 3: {passes["3"]}</p>
        <p> 2: {passes["2"]}</p>
        <p> 1: {passes["1"]}</p>
        <p> 0: {passes["0"]}</p>
        <p> Average: {average}</p>
      </div>
    </div>
  );
}

export default App;
