import { useState } from 'react';
import Session from './Session';

import React, { useRef, useEffect } from 'react';
import Chart from 'chart.js/auto';

function LineChart(sessionsList) {
    sessionsList = sessionsList["sessionsList"]
    //key = date, value = passing that day
    let my_data = {}
    for (const session of sessionsList) {
        if (!my_data.hasOwnProperty(session.date)){
            my_data[session.date] = {"total": Math.round(session.count*session.average), 
                                     "count": session.count}
        } 
        else{
            // my_data[session.date['total']] = my_data[session.date['total']] + Math.round(session.count*session.average)
            // my_data[session.date['count']] = my_data[session.date['count']] + session.count
        }
    }
    console.log(my_data)
    let values = []
    // for (const day of Object.values(my_data)){
    //     values.push(day.total/day.count)
    // }
    // console.log(values)

    const data = {
        labels: Object.keys(my_data),
        datasets: [
          {
            label: 'Serve Recieve Rating',
            data: values,
            fill: true,
            borderColor: 'rgb(75, 192, 192)',
            tension: 0.1
          }
        ]
      };
    const canvasRef = useRef(null);

    useEffect(() => {
        if (canvasRef.current) {
        const ctx = canvasRef.current.getContext('2d');

        // Instantiate a new chart using 'new Chart()'
        const lineChart = new Chart(ctx, {
            type: 'line',
            data: data,
        });

        // Clean-up function to destroy chart instance on component unmount
        return () => {
            lineChart.destroy();
        };
        }
    }, [data]); // Re-run effect if 'data' or 'options' props change

    return (
        <canvas ref={canvasRef}></canvas>
    );
}

export default LineChart;
