import { useState } from 'react';
import Session from './Session';

// Use the 'use' prefix to follow the convention for custom hooks
function useUser() {
    const [username, setUsername] = useState('');
    const [sessions, setSessions] = useState([
      new Session(new Date().toLocaleDateString()),
      new Session(new Date().toLocaleDateString()),
      new Session('11/14/2023'),
    ]);

    // 'setSessions' logic should ensure that it does not depend on calling useState during event handling.
    // It should instead use a function that handles the logic of updating sessions based on previous state.
    // const addSession = (newSession) => {
    //   setSessions((prevSessions) => [...prevSessions, newSession]);
    // };

    return { username, setUsername, sessions, setSessions};
  }

  export default useUser;
