import React, { useState } from 'react';
import Player from "./Player";

function App() {
  // Create a player state with useState
  const [player, setPlayer] = useState(new Player('TEST'));

  // Define incrementPass as a function that returns another function
  // that takes 'passKey' as a parameter
  function incrementPass(passKey) {
    return function() {
      // Assuming setPasses is a method within your Player class that correctly
      // updates the passes object and 'passes' is a part of the player's state
      player.setPasses((prevPasses) => {
        const updatedPasses = {
          ...prevPasses,
          [passKey]: prevPasses[passKey] + 1,
        };
        return updatedPasses;
      });

      // Since player is an instance of a class, you should create a new instance
      // or trigger an update in a way that React recognizes the state change.
      // Here's how you could trigger an update if Player is designed to work with setState:
      setPlayer(new Player(player.name, player.getPasses())); // You'll need to adjust this depending on your Player class design
    };
  }

  // Use an arrow function to avoid calling the function on render
  return (
    <div className="container">  
      <div className="button_container">
        <button onClick={() => incrementPass('3')}> 3 </button>
        <button onClick={() => incrementPass('2')}> 2 </button>
        <button onClick={() => incrementPass('1')}> 1 </button>
        <button onClick={() => incrementPass('0')}> 0 </button>
      </div>
      <div className="stats">
        {/* Ensure you are accessing the passes in a way that works with your state management */}
        <p> 3: {player.passes["3"]}</p>
        <p> 2: {player.passes["2"]}</p>
        <p> 1: {player.passes["1"]}</p>
        <p> 0: {player.passes["0"]}</p>
      </div>
    </div>
  );
}

export default App;
