import React, { useState } from 'react';

function App() {
  const [passes, setPasses] = useState({
    "3": 0,
    "2": 0,
    "1": 0,
    "0": 0,
  })

  const [total, setTotal] = useState(0)
  const [count, setCount] = useState(0)
  const [average, setAverage] = useState(0)

  function incrementPass(passKey) {
    setPasses((currentPasses) => {
      // Update the passes state first
      const updatedPasses = {
        ...currentPasses,
        [passKey]: currentPasses[passKey] + 1,
      };

      // Then calculate the new average based on the updated passes

      // Iterate over the updated passes to calculate total and count
      for (const key in updatedPasses) {
        setTotal(total + updatedPasses[key] * parseInt(key, 10)) ; // Parse key to be sure it's an integer
        setCount(count + updatedPasses[key]) 
      }

      // Set the average using its own state updater
      const newAverage = total / count;
      setAverage(newAverage); // This assumes average should be updated each time passes change

      // Return the updated passes
      return updatedPasses;
    });
  }




  return (
    <div className="container">  
      <div className="button_container">
        <button onClick={() => incrementPass('3')}> 3 </button>
        <button onClick={() => incrementPass('2')}> 2 </button>
        <button onClick={() => incrementPass('1')}> 1 </button>
        <button onClick={() => incrementPass('0')}> 0 </button>
      </div>
      <div className="stats">
        {/* Ensure you are accessing the passes in a way that works with your state management */}
        <p> 3: {passes["3"]}</p>
        <p> 2: {passes["2"]}</p>
        <p> 1: {passes["1"]}</p>
        <p> 0: {passes["0"]}</p>
        <p> Average: {average.toFixed(2)}</p>
      </div>
    </div>
  );
}

export default App;
