import React, { useState } from 'react';
import Player from "./Player";

function App() {
  // Create a player state with useState
  const [player, setPlayer] = useState(new Player('TEST'));


  const updatePlayer = passnum => () => {
    player.setPasses(passes => {
      return{
        ...passes,
        passnum: passes[passnum] + 1
      }

    })
    console.log(`Update for pass number: ${passnum}`);
  };

  return (
    <div className="container">  
      <div className="button_container">
        <button onClick={updatePlayer('3')}> 3 </button>
        <button onClick={updatePlayer('2')}> 2 </button>
        <button onClick={updatePlayer('1')}> 1 </button>
        <button onClick={updatePlayer('0')}> 0 </button>
      </div>
      <div className="stats">
        <p> 3: {player.passes["3"]}</p>
        <p> 2: {player.passes["2"]}</p>
        <p> 1: {player.passes["1"]}</p>
        <p> 0: {player.passes["0"]}</p>
      </div>
    </div>
  );
}

export default App;
